import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
function Recipe() {
  const { id } = useParams();
  useEffect(() => {
    getRecipe(id);
    console.log(getRecipe(id));
  }, [id]);
  const [data, setData] = useState({});

  const [recipe, setRecipe] = useState({
    steps: {
      step: "",
    },

    analyzedInstructions: [],
  });

  const getRecipe = async (id) => {
    fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e`,
      {
        headers: {
          "Content-Type": "application/json",
        },

        includeInstruction: "false",
        id: "",
      }
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res.analyzedInstructions[0].steps);
        console.log(res);
        setRecipe(res.analyzedInstructions[0].steps);
        setData(res);
      });
  };

  return (
    <div className="recipeContainer">
      {recipe.map((item) => (
        <div id={data.id}>
          <p className="recipeSteps">{item.step}</p>
        </div>
      ))}
      <img id={data.id} className="recipeImg" src={data.image} alt="dish img" />
      <span id={data.id}>{data.readyInMinutes}</span>
    </div>
  );
}

export default Recipe;
