import React, { useContext } from "react";
import { StateContext } from "./context";
import "./Order.css";

function Order() {
  const { orders } = useContext(StateContext);
  return (
    <div>
      <form>
        <input>Enter your postcode</input>
        <input>Delivery time</input>
        <input>Comments</input>
        <input>
          <ul className="orderContainer">
            {orders.length ? (
              orders.map((order) => (
                <li className="orderLi">
                  <h1 className="orderTitle">{order}</h1>
                </li>
              ))
            ) : (
              <h1 className="emptyOrder">No orders</h1>
            )}
          </ul>
        </input>
      </form>
    </div>
  );
}

export default Order;
