import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import "./recipe.css";
const Recipe = () => {
  const { id } = useParams();
  useEffect(() => {
    getRecipe(id);
  }, [id]);
  const [data, setData] = useState({
    dishTypes: [],
  });

  const [recipe, setRecipe] = useState([
    {
      steps: [{}],
    },
  ]);

  const getRecipe = async (id) => {
    fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e`,
      {
        headers: {
          "Content-Type": "application/json",
        },
        includeInstruction: "false",
        id: "",
      }
    )
      .then((res) => res.json())
      .then((result) => {
        setRecipe(result.analyzedInstructions[0].steps);

        setData(result);
        console.log(result);
      });
  };

  return (
    <div className="recipeContainer">
      <h3 className="recipeTitle">{data.title}</h3>
      <div className="recipeContent">
        <img
          id={data.id}
          className="recipeImg"
          src={data.image}
          alt="dish img"
        />

        {recipe.map((item) => (
          <div id={data.id}>
            <p className="recipeSteps">{item.step}</p>
          </div>
        ))}
        <h4>Dish type : {data.dishTypes[0]}</h4>
        <h5 id={data.id}>Ready in : {data.readyInMinutes}</h5>
        <Link className="link" to="/menu" className="closeRecipe">
          x
        </Link>
      </div>
    </div>
  );
};

export default Recipe;
