import React, { useState, useEffect } from "react";

import "./menu.css";

function Recipe({ match }) {
  useEffect(() => {
    fetchRecipe();
    console.log(match);
  }, []);

  const [item, setItem] = useState([]);

  const fetchRecipe = async () => {
    const api =
      "https://api.spoonacular.com/recipes/id=${match.params.id}/nutritionWidget.json?&apiKey=d2cd79e450d14697b5225d90ec63320e";
    fetch(api);
  };

  return (
    <div>
      <h2 className="menuTitle">Recipe</h2>
    </div>
  );
}

export default Recipe;
