import React, { useState, useEffect } from "react";
import "./Styles.css";
import "./menu.css";
import { Link } from "react-router-dom";

function Recipe() {
  useEffect(() => {
    getData();
  }, []);

  const [item, setItem] = useState({});

  const getData = async () => {
    const data =
      "https://api.spoonacular.com/recipes/random?number=10&tags=vegetarian,dessert&apiKey=66f3a4d769ef4fe49fa8a7233278bef6";
    fetch(data)
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        setItem(res.recipes);
      });
  };

  return (
    <div>
      <h2 className="menuTitle">Special menu</h2>

      <div className="centered menuContainer">
        {item.map((item, index) => (
          <div key={item.id} className="recipe">
            <figcaption className="menuImage">
              <img
                className="menuImg"
                src={item.image}
                id={item.id}
                type={item.imageType}
                alt="pic"
              />
            </figcaption>
            <div className="recipeDetails">
              <h3 className="recipe_title">{item.title}</h3>
              <h5 className="recipe_type">{item.dishTypes}</h5>
              <div className="button_container">
                <button className="menuButton">
                  <Link to={"menu/${item.id}"}>more</Link>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Recipe;
