import React, { useState, useEffect } from "react";
import "./Styles.css";

const [items, setItems] = useState([]);

function Menu() {
  useEffect(() => {
    getData();
    setItems({});
  }, []);

  const getData = async () => {
    const data = await fetch(
      "https://api.spoonacular.com/recipes/random?number=10&tags=vegetarian,dessert&apiKey=66f3a4d769ef4fe49fa8a7233278bef6"
    );

    const items = data.json();
    console.log(items);
    setItems(items);
  };

  return (
    <div className="centered">
      <h2>our menu</h2>
      {items.map((item) => (
        <h3>item.title</h3>
      ))}
    </div>
  );
}

export default Menu;
