function Recipe() {
  useEffect(() => {
    getRecipe();
  }, []);

  const [recipe, setRecipe] = useState({});

  const getRecipe = async (id) => {
    await fetch(
      "http://cors-anywhere.herokuapp.com/https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e"
    )
      .then((res) => res.json())
      .this((res) => {
        console.log(res);
        setRecipe(res);
      });
  };

  return <div>{<p>content here</p>}</div>;
}

export default Recipe;
