import React, { useState, useEffect } from "react";
import "./Styles.css";

function Menu() {
  useEffect(() => {
    getData();
  }, []);
  const [items, setItems] = useState([]);

  const getData = async () => {
    const data = await fetch(
      "https://api.spoonacular.com/recipes/random?number=10&tags=vegetarian,dessert&apiKey=66f3a4d769ef4fe49fa8a7233278bef6"
    );

    const items = data.json();
    console.log(items);
    setItems(items);
  };

  return (
    <div className="centered">
      <h2>our menu</h2>
      {items.recipes.map((item) => {
        item.title;
      })}
    </div>
  );
}

export default Menu;
