import React, { useState, useContext } from "react";
import { StateContext } from "./context";
import "./Order.css";
import { useParams } from "react-router-dom";
function Order() {
  const { id } = useParams();
  const { orders } = useContext(StateContext);
  const deleteItem = (id) => {
    console.log(id);
  };

  return (
    <div>
      {orders.length ? (
        <div>
          <form>
            <legend className="formName">My tasty journey:</legend>
            <label for="fname">First name:</label>
            <input type="text" id="fname" name="fname" />
            <br></br>
            <label for="lname">Last name:</label>
            <input type="text" id="lname" name="lname" />
            <br></br>
            <label for="day">Date:</label>
            <input type="date" id="day" name="day" />
            <br></br>
            <ul className="orderContainer">
              {orders.map((order) => (
                <li className="orderLi">
                  <h4 className="orderTitle">{order}</h4>
                  <button
                    className="deleteBtn"
                    onClick={(id) => deleteItem(id)}
                  >
                    &#10006;
                  </button>
                </li>
              ))}
            </ul>

            <input className="orderBtn" type="submit" value="Submit" />
          </form>
        </div>
      ) : (
        <h1 className="emptyOrder">No orders</h1>
      )}
    </div>
  );
}
export default Order;
