import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./recipe.css";
const Recipe = () => {
  const { id } = useParams();
  useEffect(() => {
    getRecipe(id);
  }, [id]);
  const [data, setData] = useState({});

  const [recipe, setRecipe] = useState([
    {
      steps: [{}],
    },
  ]);

  const getRecipe = async (id) => {
    fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e`,
      {
        headers: {
          "Content-Type": "application/json",
        },

        includeInstruction: "false",
        id: "",
      }
    )
      .then((res) => res.json())
      .then((result) => {
        setRecipe(result.analyzedInstructions[0].steps);

        setData(result);
      });
  };

  return (
    <div className="recipeContainer">
      <div className="recipeContent">
        {recipe.map((item) => (
          <div id={data.id}>
            <p className="recipeSteps">{item.step}</p>
          </div>
        ))}
        <img
          id={data.id}
          className="recipeImg"
          src={data.image}
          alt="dish img"
        />
        <span id={data.id}>{data.readyInMinutes}</span>
      </div>
    </div>
  );
};

export default Recipe;
