import React, { useState, useEffect } from "react";
import "./Styles.css";
import "./menu.css";
import { Link } from "react-router-dom";

function Recipe() {
  useEffect(() => {
    getData();
  }, []);

  const [item, setItem] = useState([]);

  const getData = async () => {
    const data =
      "https://api.spoonacular.com/recipes/random?number=10&tags=vegetarian,dessert&apiKey=66f3a4d769ef4fe49fa8a7233278bef6";
    fetch(data)
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        setItem(res.recipes);
      });
  };

  return (
    <div>
      <h2 className="menuTitle">Recipe</h2>

      <div className="centered menuContainer">
        {item.map((recipe, index) => (
          <div key={recipe.id} className="recipe">
            <img
              className="menuImg"
              src={recipe.image}
              id={recipe.id}
              type={recipe.imageType}
              alt="pic"
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Recipe;
