import React, { useState, useEffect } from "react";
import "./Styles.css";

function Menu() {
  useEffect(() => {
    getData();
  }, []);

  const [items, setItems] = useState([]);

  const getData = async () => {
    const data =
      "https://api.spoonacular.com/recipes/random?number=10&tags=vegetarian,dessert&apiKey=66f3a4d769ef4fe49fa8a7233278bef6";
    fetch(data)
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        setItems(res.recipes);
      });
  };

  return (
    <div className="centered menuContainer">
      {items.map((item, index) => (
        <div key={item.id} className="recipe">
          <img src={item.image} id={item.id} type={item.imageType} />
          <div className="recipeDetails">
            <h3 className="title">{item.title}</h3>
            <h4 className="type">{item.dishTypes}</h4>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Menu;
