import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
function Recipe() {
  const [recipe, setRecipe] = useState({
    extendedIngredients: [],
  });
  const { id } = useParams();

  useEffect(() => {
    fetchRecipe(id);
  }, [id]);
  const fetchRecipe = async (id) => {
    await fetch(
      `https://api.spoonacular.com/recipes/${id}/information?apiKey=d2cd79e450d14697b5225d90ec63320e`
    )
      .then((res) => res.blob())
      .then((res) => {
        console.log(res);
        setRecipe(res);
      });
  };
  return (
    <div>
      {
        <div>
          <div>
            <img src={recipe.image} alt="pic" />
            <span>{recipe.readyInMinutes}</span>
            <div>
              {recipe.extendedIngredients.map((ingredient) => (
                <div className="ingredientDiv" id={ingredient.id}>
                  <h4>{ingredient.aisle}</h4>

                  <img
                    className="ingredientImg"
                    src={ingredient.image}
                    alt="picture"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      }
    </div>
  );
}

export default Recipe;
