import React, { useState, useEffect } from "react";

import "./menu.css";

function Recipe() {
  useEffect(() => {
    getRecipe();
  }, []);

  const [item, setItem] = useState([]);
  const getRecipe = async (id) => {
    await fetch(
      ` "https://api.spoonacular.com/recipes/${id}&apiKey=66f3a4d769ef4fe49fa8a7233278bef6";`
    );
  };

  return (
    <div>
      <h2 className="menuTitle">Recipe</h2>
    </div>
  );
}

export default Recipe;
