import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./menu.css";

function Recipe() {
  useEffect(() => {
    getRecipe(id);
  }, []);

  const [item, setItem] = useState([]);
  const getRecipe = async (id) => {
    await fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=66f3a4d769ef4fe49fa8a7233278bef6;`
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res.result);
      });
  };

  return (
    <div>
      <h2 className="menuTitle">Recipe</h2>
    </div>
  );
}

export default Recipe;
