import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
function Recipe() {
  const { id } = useParams();
  useEffect(() => {
    getRecipe(id);
    console.log(getRecipe(id));
  }, [id]);

  const [recipe, setRecipe] = useState([]);

  const getRecipe = async (id) => {
    fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e`,
      {
        headers: {
          "Content-Type": "application/json",
        },

        includeInstruction: "false",
        id: "",
      }
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res.analyzedInstructions[0]);
        setRecipe(res.analyzedInstructions[0]);
      });
  };

  return <div></div>;
}

export default Recipe;
