import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import "./recipe.css";
const Recipe = () => {
  const { id } = useParams();
  useEffect(() => {
    getRecipe(id);
  }, [id]);
  const [data, setData] = useState({
    dishTypes: [],
  });

  const [recipe, setRecipe] = useState([
    {
      steps: [{}],
    },
  ]);

  const getRecipe = async (id) => {
    fetch(
      `https://api.spoonacular.com/recipes/${id}/information?&apiKey=d2cd79e450d14697b5225d90ec63320e`,
      {
        headers: {
          "Content-Type": "application/json",
        },
        includeInstruction: "false",
        id: "",
      }
    )
      .then((res) => res.json())
      .then((result) => {
        setRecipe(result.analyzedInstructions[0].steps);

        setData(result);
        console.log(result);
      });
  };

  return (
    <div className="recipeContainer">
      <div className="recipeContent">
        <h2 className="recipeTitle">{data.title}</h2>
        <p id={data.id} className="recipeSteps">
          <svg className="heart">
            <clipPath id="heart-clip">
              <path
                id="heart"
                d="M248.078,5.883c-36.691-14.739-77.771-0.839-98.517,31.125C128.817,5.044,87.735-8.856,51.043,5.883 C9.354,22.632-10.863,70.009,5.887,111.696c16.06,39.98,143.314,139.607,143.314,139.607l0.359,0.28l0.36-0.28 c0,0,127.251-99.627,143.314-139.607C309.986,70.009,289.768,22.632,248.078,5.883z"
              />
            </clipPath>
            <img
              id={data.id}
              className="recipeImg"
              src={data.image}
              alt="dish img"
            />
          </svg>

          {recipe.map((item) => (
            <span className="spanSteps">{item.step}</span>
          ))}

          <h4 className="recipeType">Dish type : {data.dishTypes[0]}</h4>
          <h5 id={data.id}>Total time : {data.readyInMinutes} min</h5>
        </p>
        <Link className="link" to="/menu" className="closeRecipe">
          x
        </Link>
      </div>
    </div>
  );
};

export default Recipe;
